import { useState, useEffect } from "react"
import useInput from '../customHooks/useInput'

const SimpleInput = (props) => {


  const {
    value: enteredNameValue, 
    hasError: nameInputError,
    valueChangedHandler: nameChangeHandler,
    InputBlurHandler: nameInputBlurHandler,
    isValid: valueIsValid, 
    reset: resetInput
  } = useInput(value => value.trim() !== "")

  const {
    value: enteredEmailValue, 
    hasError: emailInputError,
    valueChangedHandler: emailChangeHandler,
    InputBlurHandler: emailInputBlurHandler,
    isValid: emailIsValid, 
    reset: resetEmail
  } = useInput(value => value.includes('@'))





  const formSubmitHandler = (event) => {
    event.preventDefault()
    nameInputBlurHandler(true)

    if (!emailIsValid) {
      return
    }
    resetInput()
    resetEmail()
  }


  const nameInputClasses = nameInputError ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'> Your Name </label>
        <input
          type='text'
          id='name'
          value={enteredNameValue}
          onChange={nameChangeHandler}
          onBlur={nameInputBlurHandler} />
           <input
          type='email'
          id='email'
          value={enteredEmailValue}
          onChange={emailChangeHandler}
          onBlur={emailInputBlurHandler} />
        {nameInputError && emailInputError && <p className={"error-text"}> Cannot be empty </p>}
      </div>
      <div className="form-actions">
        <button disabled={!valueIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
