import { useState, useEffect } from "react"
import useInput from '../customHooks/useInput'

const SimpleInput = (props) => {

  let valid = false
  const validateValue = (value) => {
    valid = value !== ""
  }

  const { 
    value, hasError,
    setEnteredValue,
    setTouched,
    valueChangedHandler, InputBlurHandler,

  } = useInput(validateValue)









  const formSubmitHandler = (event) => {
    event.preventDefault()
    InputBlurHandler(true)

    if (!valid) {
      return
    }




    setEnteredValue('')
    setTouched(false)
  }


  const nameInputClasses = !valid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'> Your Name </label>
        <input type='text' id='name' value={value} onChange={valueChangedHandler} onBlur={nameInputBlurHandler} />
        {hasError && <p className={"error-text"}> Cannot be empty </p>}
      </div>
      <div className="form-actions">
        <button disabled={!valid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
