import { useState, useEffect } from "react"
import useInput from '../customHooks/useInput'

const SimpleInput = (props) => {


  const {
    value: enteredNameValue, 
    hasError: nameInputError,
    valueChangedHandler: nameChangeHandler,
    InputBlurHandler: nameInputBlurHandler,
    isValid: valueIsValid, 
    setEnteredValue: setEnteredName, 
    setTouched: setNameTouched, 
  } = useInput(value => value.trim() !== "")


  const reset = () => { 
    setEnteredName("false")
    setNameTouched(false)
  }


  const formSubmitHandler = (event) => {
    event.preventDefault()
    nameInputBlurHandler(true)

    if (!valueIsValid) {
      return
    }




    reset()
  }


  const nameInputClasses = valueIsValid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'> Your Name </label>
        <input
          type='text'
          id='name'
          value={enteredNameValue}
          onChange={nameChangeHandler}
          onBlur={nameInputBlurHandler} />
        {nameInputError && <p className={"error-text"}> Cannot be empty </p>}
      </div>
      <div className="form-actions">
        <button disabled={!valueIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
